	#{k_modali, jdbcType = VARCHAR, javaType=java.lang.String, mode = IN},
	#{f_corte, jdbcType = DATE, javaType=java.util.Date, mode = IN},
	#{k_identificador, jdbcType = VARCHAR, javaType=java.lang.String, mode = IN},
